import { useEffect, useState } from 'react';
import './App.css';
import Web3 from 'web3';

function App() {

  const [web3Api, setWeb3Api] = useState ({
    provider: null,
    web3 : null
  })

  const [account, setAccount] = useState (null)
  const [accountBalance , setAccountBalance] = useState ("0")

  useEffect(() => {
    const loadProvider = async () => {
      let provider = null;

      if (window.ethereum){
        provider = window.ethereum;
        try {
          await provider.enable();
        } catch {
          console.error("users denied accounts access")
        }
      } else if (window.web3)
      {
        provider = window.web3.currentProvider

      }else if (!process.env.production)
      {
        provider = new Web3.providers.HttpProvider("http://localhost:7545")
      }

      setWeb3Api 
      (
        {
          web3 : new Web3(provider),
          provider
        }
      )

    }
    loadProvider()
  }, [])

  useEffect (() =>
  {
    const getAccount = async () => 
    {
      const accounts = await web3Api.web3.eth.getAccounts()
      setAccount(accounts[0])

    }

    const getBalance = async () =>
    {
      const balances = await webApi.web3.eth.getBalance(account)
      setAccountBalance(balances[0])
    }

    web3Api.web3 && getAccount()
  }, [web3Api.web3])

  console.log (balances[0])
  return (
    <>
      <dev className="faucet-wrapper">
        <dev className="faucet">
          <span>
            <strong>account :</strong>
            <h1>
              {account ? account : "not connected"}
            </h1>
          </span>
          <dev className="balance-view is-size-2">
            Current Balance : <strong>{accountBalance}</strong> Eth
          </dev>
          <button className="btn mr-2">Donate</button>
          <button className="btn">Withdraw</button>
        </dev>
      </dev>
    </>
  );
}

export default App;
